<?php

namespace FOS\UserBundle\Entity;

use Doctrine\ORM\Mapping as ORM;

/**
 * symfonyBiblio
 *
 * @ORM\Table(name="symfony_biblio")
 * @ORM\Entity(repositoryClass="FOS\UserBundle\Repository\symfonyBiblioRepository")
 */
class symfonyBiblio
{
    /**
     * @var int
     *
     * @ORM\Column(name="id", type="integer")
     * @ORM\Id
     * @ORM\GeneratedValue(strategy="AUTO")
     */
    private $id;

    /**
     * @var string
     *
     * @ORM\Column(name="user", type="string", length=255)
     */
    private $user;


    /**
     * Get id
     *
     * @return int
     */
    public function getId()
    {
        return $this->id;
    }

    /**
     * Set user
     *
     * @param string $user
     *
     * @return symfonyBiblio
     */
    public function setUser($user)
    {
        $this->user = $user;

        return $this;
    }

    /**
     * Get user
     *
     * @return string
     */
    public function getUser()
    {
        return $this->user;
    }
}

